import 'package:flutter/material.dart';
import 'package:flutter/src/widgets/container.dart';
import 'package:flutter/src/widgets/framework.dart';

class CheckBoxAssign extends StatefulWidget {
  const CheckBoxAssign({super.key});

  @override
  State<CheckBoxAssign> createState() => _CheckBoxAssignState();
}

class _CheckBoxAssignState extends State<CheckBoxAssign> {
  bool ischecked = false;

  List datas = [
    [
      'data:image/png;base64,',
      'Android',
      false
    ],
    [
      'data:image/png;base64,',
      'Flutter',
      false
    ],
    [
      'data:image/png;base64,',
      'Apple',
      false
    ],
    [
      'data:image/png;base64,',
      'Php',
      false
    ],
    [
      'data:image/png;base64,',
      'Node',
      false
    ]
  ];
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: Text("CheckBox Assignment")),
      body: ListView.builder(
        itemCount: datas.length,
        itemBuilder: (context, index) {
          return Padding(
            padding: const EdgeInsets.symmetric(horizontal: 10, vertical: 5),
            child: Card(
              child: CheckboxListTile(
                value: datas[index][2],
                onChanged: (value) {
                  setState(() {
                    datas[index][2] = value!;
                  });
                },
                activeColor: Colors.blueGrey,
                secondary: SizedBox(
                  width: 60,
                  child: Padding(
                    padding: const EdgeInsets.all(8.0),
                    child: Image.network(
                      datas[index][0],
                      fit: BoxFit.contain,
                      height: 50,
                    ),
                  ),
                ),
                title: Text(datas[index][1]),
              ),
            ),
          );
        },
      ),
    );
  }
}

void main() {
  runApp(MaterialApp(
    home: CheckBoxAssign(),
  ));
}
